mapper.select:

select rno, post_id, reply, admin_name, replydate, updatedate
from
   (
     select /*+INDEX(tbl_reply idx_reply) */
        rownum rn,  rno, post_id, reply, admin_name, replyDate, updatedate
   from tbl_reply
   where post_id =  #{post_id}
                  and rno > 0 
                  and rownum <= #{cri.pageNum} * #{cri.amount}
) where rn > (#{cri.pageNum} -1) * #{cri.amount}
